mapper.select:
/*[qna.xml][getQnaList][문의 목록 조회][sungjun.Cho]*/ SELECT QNA_NO as 'QNA_NO' , QNA_TITLE , QNA_CONTENT , DATE_FORMAT(QNA_DATE , '%Y.%m.%d') AS 'QNA_DATE' , USER_ID , COMMENT , CHECK_COMMENT FROM QNA Q JOIN MEMBER M USING(USER_NO)
WHERE QNA_TITLE LIKE CONCAT('%', #{serch}, '%')
ORDER BY QNA_NO DESC

mapper.insert:
/*[qna.xml][insertQna][문의 작성][sungjun.Cho]*/
INSERT
  INTO QNA
       (QNA_TITLE
     , QNA_CONTENT
     , O_FILE_NAME
     , C_FILE_NAME
     , USER_NO)
VALUES (#{qna_title}
     , #{qna_content}
     , #{o_file_name}
     , #{c_file_name}
     , #{user_no})

mapper.select:
/*[qna.xml]["myQnaListCount"][마이페이지 문의 목록 페이징][sungjun.Cho]*/
SELECT COUNT(QNA_NO)
  FROM QNA
 WHERE USER_NO = #{user_no}

mapper.delete:
/*[qna.xml][qnaDelete][문의 삭제][sungjun.Cho]*/
DELETE
  FROM QNA
 WHERE QNA_NO = #{qno}

mapper.update:
/*[qna.xml][updateQna][문의 수정][sungjun.Cho]*/
UPDATE QNA
   SET QNA_TITLE = #{qna_title}
     , QNA_CONTENT = #{qna_content}
     , QNA_MODIFYDATE = NOW()
     , QNA_MODIFYER = #{user_id}

     , O_FILE_NAME = #{o_file_name}
     , C_FILE_NAME = #{c_file_name}

 WHERE QNA_NO = #{qna_no}
